mapper.select:
SELECT ap.no, ap.orders_no AS ordersNo, ap.user_no AS userNo, l.name AS advertiserName, ap.ad_path AS adPath, ap.ad_type AS adType, ap.main_text AS mainText, ap.detail_text AS detailText, ap.tip_text AS tipText, ap.start_date AS startDate, ap.end_date AS endDate, ap.ad_status AS adStatus, ap.approval_status AS approvalStatus, ap.created_at AS createdAt, ap.updated_at AS updatedAt FROM ad_purchase ap LEFT JOIN lawyer l ON ap.user_no = l.no WHERE 1=1
AND ap.ad_type = #{adType}
AND ap.ad_status = #{adStatus}
AND ap.approval_status = #{approvalStatus}
AND l.name LIKE CONCAT('%', #{keyword}, '%')
ORDER BY ap.start_date DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM ad_purchase ap
LEFT JOIN lawyer l ON ap.user_no = l.no
WHERE 1=1


    AND ap.ad_type = #{adType}


    AND ap.ad_status = #{adStatus}


    AND ap.approval_status = #{approvalStatus}


    AND l.name LIKE CONCAT('%', #{keyword}, '%')
