mapper.select:
SELECT o.no AS order_no, o.status, DATE_FORMAT(o.created_at, '%Y-%m-%d') AS order_date, o.total_amount,
-- 대표 템플릿명 가져오기 (서브쿼리) (SELECT t.name FROM tmpl_orders_history toh JOIN template t ON toh.tmpl_no = t.no WHERE toh.order_no = o.no ORDER BY toh.no ASC LIMIT 1) AS first_template_name,
-- 전체 상품 수 (n개 상품 만들기용) (SELECT COUNT(*) FROM tmpl_orders_history toh WHERE toh.order_no = o.no) AS template_count
FROM orders o WHERE o.user_no = #{userNo} AND o.order_type = 'TEMPLATE'
AND o.status = #{status}
ORDER BY o.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM orders o
WHERE o.user_no = #{userNo}
AND o.order_type = 'TEMPLATE'

    AND o.status = #{status}

SELECT COUNT(*)
FROM tmpl_orders_history toh
JOIN template t ON toh.tmpl_no = t.no
WHERE toh.order_no = #{orderNo}
AND t.type = #{type}
AND t.category_no = #{categoryNo}
AND toh.is_downloaded = #{isDownloaded}

mapper.update:
UPDATE tmpl_orders_history
SET is_downloaded = 1
WHERE order_no = #{orderNo}
  AND tmpl_no = #{tmplNo}

mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM tmpl_orders_history
    WHERE order_no = #{orderNo}
      AND is_downloaded = 1
)

SELECT EXISTS (
    SELECT 1
    FROM tmpl_orders_history
    WHERE order_no = #{orderNo}
      AND tmpl_no = #{tmplNo}
      AND is_downloaded = 1
)
