mapper.select:
SELECT COUNT(*)
FROM lawyer
WHERE lawyer_id = #{lawyerId}

SELECT COUNT(*)
FROM client
WHERE client_id = #{lawyerId}

mapper.insert:
INSERT INTO lawyer (
    no, lawyer_id, pw_hash, profile , email, name,office_number ,phone,
    ,zipcode,road_address,land_adress,detail_adress,point,content,status ,consult_price,office_name,
) VALUES (
             #{no}, #{lawyerId}, #{pw_hash}, #{profile}, #{email},#{name},
             #{office_number},#{phone},#{zip_code},#{land_address},#{detail_address},
             #{point},#{content},#{status},#{consult_price},#{office_name}
         )
