mapper.select:
SELECT t.no, t.user_no AS userNo, t.category_no AS categoryNo, t.type, c.name AS category_name, t.name, t.price, t.discount_rate, t.thumbnail_path, t.sales_count, t.created_at FROM template t LEFT JOIN category c ON t.category_no = c.no WHERE t.user_no = #{lawyerNo} AND t.is_deleted = 0 AND t.category_no = #{categoryNo} AND t.name LIKE CONCAT('%', #{keyword}, '%') AND t.type = #{type} ORDER BY
t.sales_count DESC t.price ASC t.price DESC t.created_at DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM template t
WHERE t.user_no = #{lawyerNo}
AND t.is_deleted = 0
AND t.category_no = #{categoryNo}
AND t.name LIKE CONCAT('%', #{keyword}, '%')
AND t.type = #{type}
