mapper.insert:
INSERT INTO payments (
    order_no, payment_key, order_code, amount, status,
    card_company, installment_month, purchased_at, pg, metadata
)
VALUES (
           #{orderNo}, #{paymentKey}, #{orderCode}, #{amount}, #{status},
           #{cardCompany}, #{installmentMonth}, #{purchasedAt}, #{pg}, #{metadata, jdbcType=VARCHAR}
       )

mapper.select:
SELECT no
FROM payments
WHERE payment_key = #{paymentKey}

mapper.insert:
INSERT INTO refunds (
    payment_no, refund_key, amount, status, reason,
    refunded_at, metadata
) VALUES (
             #{paymentNo}, #{refundKey}, #{amount}, #{status}, #{reason},
             #{refundedAt}, #{metadata, jdbcType=VARCHAR}
         )

mapper.select:
SELECT order_no
FROM payments
WHERE payment_key = #{paymentKey}

SELECT payment_key
FROM payments
WHERE order_no = #{orderNo}

mapper.update:
UPDATE payments
SET status = #{status}
WHERE order_no = #{orderNo}
