mapper.select:
SELECT * FROM (
SELECT l.no, l.profile, l.name, l.consult_price AS consultPrice, l.office_name AS officeName, l.lawyer_intro AS lawyerIntro, (
CASE WHEN l.name LIKE CONCAT('%', #{kw}, '%') THEN 10 WHEN l.office_name LIKE CONCAT('%', #{kw}, '%') THEN 5 WHEN l.lawyer_intro LIKE CONCAT('%', #{kw}, '%') THEN 3 ELSE 0 END
) AS score, l.point AS point, (SELECT COUNT(*) FROM template t WHERE t.user_no = l.no) AS templateCount, (SELECT COUNT(*) FROM comment c WHERE c.user_no = l.no) AS qnaAnswerCount, 1 AS grp FROM lawyer l WHERE l.status = 'APPROVED_JOIN'
AND (
l.name LIKE CONCAT('%', #{kw}, '%') OR l.office_name LIKE CONCAT('%', #{kw}, '%') OR l.lawyer_intro LIKE CONCAT('%', #{kw}, '%')
)
UNION ALL
SELECT l.no, l.profile, l.name, l.consult_price AS consultPrice, l.office_name AS officeName, l.lawyer_intro AS lawyerIntro, 0 AS score, l.point AS point, (SELECT COUNT(*) FROM template t WHERE t.user_no = l.no) AS templateCount, (SELECT COUNT(*) FROM comment c WHERE c.user_no = l.no) AS qnaAnswerCount, 2 AS grp FROM lawyer l WHERE l.status = 'APPROVED_JOIN' AND l.no NOT IN ( SELECT l2.no FROM lawyer l2 WHERE l2.status = 'APPROVED_JOIN'
AND (
l2.name LIKE CONCAT('%', #{kw}, '%') OR l2.office_name LIKE CONCAT('%', #{kw}, '%') OR l2.lawyer_intro LIKE CONCAT('%', #{kw}, '%')
)
) ) AS combined ORDER BY grp ASC, score DESC, point DESC LIMIT 10

SELECT * FROM (
SELECT t.no, t.user_no AS userNo, t.category_no AS categoryNo, t.type, t.name, t.description, t.price, t.thumbnail_path AS thumbnailPath, t.discount_rate AS discountRate, (
CASE WHEN t.name LIKE CONCAT('%', #{kw}, '%') THEN 10 WHEN t.description LIKE CONCAT('%', #{kw}, '%') THEN 5 ELSE 0 END
) AS score, 1 AS grp FROM template t
t.category_no = #{category}
AND (
t.name LIKE CONCAT('%', #{kw}, '%') OR t.description LIKE CONCAT('%', #{kw}, '%')
)
UNION ALL
SELECT t.no, t.user_no, t.category_no, t.type, t.name, t.description, t.price, t.thumbnail_path, t.discount_rate, (
CASE WHEN t.name LIKE CONCAT('%', #{kw}, '%') THEN 10 WHEN t.description LIKE CONCAT('%', #{kw}, '%') THEN 5 ELSE 0 END
) AS score, 2 AS grp FROM template t
t.no NOT IN ( SELECT t1.no FROM template t1
t1.category_no = #{category}
AND (
t1.name LIKE CONCAT('%', #{kw}, '%') OR t1.description LIKE CONCAT('%', #{kw}, '%')
)
)
AND (
t.name LIKE CONCAT('%', #{kw}, '%') OR t.description LIKE CONCAT('%', #{kw}, '%')
)
AND t.category_no != #{category}
UNION ALL
SELECT t.no, t.user_no, t.category_no, t.type, t.name, t.description, t.price, t.thumbnail_path, t.discount_rate, 0 AS score, 3 AS grp FROM template t WHERE t.no NOT IN ( SELECT t2.no FROM template t2
t2.category_no = #{category}
AND (
t2.name LIKE CONCAT('%', #{kw}, '%') OR t2.description LIKE CONCAT('%', #{kw}, '%')
)
) ) AS combined ORDER BY grp ASC, score DESC, no DESC LIMIT 10

SELECT * FROM (
SELECT b.no, b.category_no AS categoryNo, b.user_no AS userNo, b.title, b.content, (
CASE WHEN b.title LIKE CONCAT('%', #{kw}, '%') THEN 10 WHEN b.content LIKE CONCAT('%', #{kw}, '%') THEN 5 ELSE 0 END
) AS score, 1 AS grp FROM board b
b.category_no = #{category}
AND (
b.title LIKE CONCAT('%', #{kw}, '%') OR b.content LIKE CONCAT('%', #{kw}, '%')
)
UNION ALL
SELECT b.no, b.category_no, b.user_no, b.title, b.content, (
CASE WHEN b.title LIKE CONCAT('%', #{kw}, '%') THEN 10 WHEN b.content LIKE CONCAT('%', #{kw}, '%') THEN 5 ELSE 0 END
) AS score, 2 AS grp FROM board b
b.no NOT IN ( SELECT b1.no FROM board b1
b1.category_no = #{category}
AND (
b1.title LIKE CONCAT('%', #{kw}, '%') OR b1.content LIKE CONCAT('%', #{kw}, '%')
)
)
AND (
b.title LIKE CONCAT('%', #{kw}, '%') OR b.content LIKE CONCAT('%', #{kw}, '%')
)
AND b.category_no != #{category}
UNION ALL
SELECT b.no, b.category_no, b.user_no, b.title, b.content, 0 AS score, 3 AS grp FROM board b WHERE b.no NOT IN ( SELECT b2.no FROM board b2
b2.category_no = #{category}
AND (
b2.title LIKE CONCAT('%', #{kw}, '%') OR b2.content LIKE CONCAT('%', #{kw}, '%')
)
) ) AS combined ORDER BY grp ASC, score DESC, no DESC LIMIT 10
