mapper.update:
UPDATE weekly_time_slots
SET status    = #{status}
WHERE user_no  = #{userNo}
  AND slot_date = #{slotDate}
  AND slot_time = #{slotTime}

mapper.select:
SELECT amount
FROM weekly_time_slots
WHERE no = #{slotNo}

SELECT no, user_no AS userNo, slot_date AS slotDate, slot_time AS slotTime, status, amount
FROM weekly_time_slots
WHERE no = #{slotNo}
