mapper.select:
SELECT v.no AS vod_no, v.broadcast_no, bs.no AS schedule_no, bs.name AS title, c.name AS category_name, bs.thumbnail_path, l.name AS lawyer_name, l.profile AS lawyer_profile, v.vod_path, v.duration, v.view_count, v.created_at FROM broadcast_vod v JOIN broadcast b ON v.broadcast_no = b.no JOIN broadcast_schedule bs ON b.schedule_no = bs.no JOIN category c ON bs.category_no = c.no JOIN lawyer l ON b.user_no = l.no WHERE v.status = 0
AND c.no = #{categoryNo}
ORDER BY v.view_count DESC
ORDER BY v.created_at DESC
LIMIT #{size} OFFSET #{offset}

mapper.update:
UPDATE broadcast_vod
SET view_count = view_count + 1
WHERE no = #{vodNo}

mapper.select:
SELECT name
FROM lawyer
WHERE no = #{userNo}
